  //     setPieChartExtremeURL(graphUrlPieExtreme);
  //     // const exreme_response = await pieChart_response_extreme.json();
  //     // setExtreme(exreme_response)
  //   }
  //   fetchImage()
  //   fetchPieChart()
  //   fetchPieChartExtreme()
  // }, [commentsList])


  return (

    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} />
        <button type="submit">Get Video Details</button>
      </form>
    {videoData && comments && commentsList && eight &&(
      <Box sx={{ height: '25%' }}>
      <Grid container >
        <Grid item xs={6}>
        <h2>{videoData.title}</h2>
          <p>Views: {videoData.viewCount}</p>
          <p>Likes: {videoData.likeCount}</p>
          <p>Comments: {videoData.commentCount}</p>
          {videoData.videoId && <YoutubeEmbed embedId={videoData.videoId} />}
        </Grid>
        <Grid item xs={6}>
          {wordCloudURL && <img src={wordCloudURL} alt="Word Cloud" width={750} height={700}/>}
        </Grid>
        <Grid item xs={6}>
        <DataTable
            columns={columns}
            data={comments}
        />
        {/* <div style={{ height: 400, width: '100%' }}>
          <DataGrid
            rows={[{comment: "やった", exreme_emotion: "positive", eight_emotion: "Joy", "likeCount": 123, "updatedAt": "2020"}]}
            columns={[
              { field: 'comment', headerName: 'コメント', width: 70 },
              {field: "extreme_emotion", headerName: "ポジ/ネガ/中立", width: 70},
              {field: "eight_emotion", headerName: "感情分類", width: 70},
