import React, { useState, useEffect } from 'react';
import YoutubeEmbed from "./youtube_embed";
import WordCloud from 'react-d3-cloud';

const App = () => {
  const [videoData, setVideoData] = useState(null);
  const [url, setUrl] = useState('');
  const [comments, setComment] = useState(null);
  const [commentsList, setCommentsList] = useState(null);
  const [wordCloud, setWordCloud] = useState(null);
  const [graphURL, setGraphURL] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Send the video URL to the Flask backend
    const response_video = await fetch('http://127.0.0.1:5000/api/video-details', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ url }),
    });

    // Retrieve the video details from the response
    const data = await response_video.json();
    setVideoData(data);

    const response_comment = await fetch('http://127.0.0.1:5000/api/video-comments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ url }),
    })
    // .then(response => {return response.json()})
    // .then(resonse_json => {return Object.values(resonse_json)})
    // .then(response_list => {response_list.map(e => tmp.push(e.comment)), setComment(response_list)})
    // .then(() => setCommentsList(tmp))


    // Retrieve the comment from the response
    const comment_response = await response_comment.json();
    const comment_response_lst = Object.values(comment_response)

    const tmp = []
    comment_response_lst.map(e => tmp.push(e.comment))
    setComment(comment_response_lst);
    setCommentsList(tmp);



  //   useEffect(() => {

  // }, [comments]);




    // const response_wordCloud = await fetch('/api/video-comments-wordCloud', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'image/png',
    //   },
    //   body: JSON.stringify({ comments }),
    // }).then(response => response.blob())
    // .then(blob => {
    //   // Convert blob to URL
    //   const graphUrl = URL.createObjectURL(blob);
    //   setGraphURL(graphUrl);
    // })


  };

  useEffect(() => {
    const fetchImage = async () => {
      const some_response = await fetch('http://127.0.0.1:5000/api/video-comments-wordCloud', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ commentsList }),
      });
      const a = await some_response.json();
      setWordCloud(a)
    }
    fetchImage()
  }, [commentsList])



  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} />
        <button type="submit">Get Video Details</button>
      </form>

      {videoData && comments &&(
        <div>
          <h2>{videoData.title}</h2>
          <p>Views: {videoData.viewCount}</p>
          <p>Likes: {videoData.likeCount}</p>
          <p>Comments: {videoData.commentCount}</p>
          {videoData.videoId && <YoutubeEmbed embedId={videoData.videoId} />}
          {comments.map(e => <li>{e.comment} {e.likeCount} {e.updatedAt}</li>)}
          <p>{commentsList}</p>
          {graphURL && <img src={graphURL} alt="Word Cloud" />}
          {graphURL}
          {wordCloud && <p>{wordCloud}</p>}
          {/* <WordCloud
            width={1000}
            height={400}
            data={["こんにちは", "うん"]}
            fontSize={8}
            padding={3}
            spiral="rectangular"
          /> */}
        {/* {comment.map(comment=> <p>{comment}</p>)} */}
        {/* <p>{comments}</p> */}
        {/* {comments.comment.map((comment, index) => (
        <div key={index}>
          <p>{comment}</p>
          <p>{comments.likeCount[index]}</p>

        </div>
        ))} */}

         {/* {comment.comment.map(comment => <li>{comment}</li>)} 
         {comment.likeCount.map(comment => <li>{comment}</li>)}  */}
                {/* comment.map(Object.values(comment) => 
                <li>{comment.comment}</li>
                <div>{comment.likeCount}</div>
                ) */}

          {/* {comment && <p>{comment}</p>} */}
        </div>
      )}
    </div>
  );
};

export default App;
