  //     });
  //     const blob = await word_cloud_response.blob();
  //     const graphUrlCloud = URL.createObjectURL(blob);
  //     setWordCloudURL(graphUrlCloud);
  //   }
  //   const fetchPieChart = async () => {
  //     const pieChart_response = await fetch('http://127.0.0.1:5000/api/comments-emotion', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ commentsList }),
  //     });
  //     const blob = await pieChart_response.blob();
  //     const graphUrlPie = URL.createObjectURL(blob);
  //     setPieChartURL(graphUrlPie);
  //   }
  //   const fetchPieChartExtreme = async () => {
  //     const pieChart_response_extreme = await fetch('http://127.0.0.1:5000/api/comments-emotion-extreme', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({ commentsList }),
  //     });
  //     const blob = await pieChart_response_extreme.blob();
  //     const graphUrlPieExtreme = URL.createObjectURL(blob);
  //     setPieChartExtremeURL(graphUrlPieExtreme);
  //     // const exreme_response = await pieChart_response_extreme.json();
  //     // setExtreme(exreme_response)
  //   }
  //   fetchImage()
  //   fetchPieChart()
  //   fetchPieChartExtreme()
  // }, [commentsList])


  return (

    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} />
        <button type="submit">Get Video Details</button>
      </form>
    {videoData && comments && commentsList && eight &&(
      <Box sx={{ height: '25%' }}>
      <Grid container >
        <Grid item xs={6}>
        <h2>{videoData.title}</h2>
          <p>Views: {videoData.viewCount}</p>
          <p>Likes: {videoData.likeCount}</p>
          <p>Comments: {videoData.commentCount}</p>
          {videoData.videoId && <YoutubeEmbed embedId={videoData.videoId} />}
        </Grid>
        <Grid item xs={6}>
          {wordCloudURL && <img src={wordCloudURL} alt="Word Cloud" width={750} height={700}/>}
        </Grid>
        <Grid item xs={6}>
        {comments.map(e => <li>{e.comment} {e.likeCount} {e.updatedAt} {e.extremeEmotion} {e.eightEmotion}</li>)}
        </Grid>
        <Grid item xs={6}>
          {pieChartURL && <img src={pieChartURL} alt="Pie Chart" />}
        </Grid>
        <Grid item xs={6}>
          {PieChartExtremeURL && <img src={PieChartExtremeURL} alt="Pie Chart" />}
        </Grid>
      </Grid>
    </Box>
    )}
      {videoData && comments && (
        <div>




          {/* <PieChart
            series={[
              {
